<?php

namespace WO\MainBundle\Entity;

use Doctrine\ORM\EntityRepository;

/**
 * ServiceRepository
 *
 * This class was generated by the Doctrine ORM. Add your own custom
 * repository methods below.
 */
class ServiceRepository extends EntityRepository
{
    /**
     * findet Services Ã¼ber den Catgory Slug und wenn sie online sind
     * @param null $slug
     * @return array
     */
    public function findByCategory($slug = null, $online = true) {
        $q = $this
            ->createQueryBuilder('s')
            ->select('s')
            ->leftJoin('s.category', 'c')
            ->where('c.slug = :slug')
            ->andWhere('s.show_online= :true')
            ->andWhere('c.show_online= :truee')
            ->setParameter('slug', $slug)
            ->setParameter('true', $online)
            ->setParameter('truee', $online)
            ->getQuery();

        $services = $q->getArrayResult();

        return $services;
    }
}
